import express from 'express'
import userModelMin from '../Controllers/user.controller.min.js'
import auth from '../Middleware/auth.min.js'

const userRouter = express.Router()

userRouter.post('/register', userModelMin.register)
userRouter.post('/login', userModelMin.login)
userRouter.post('/addmovie1', auth.verifytoken, userModelMin.addMovie)
userRouter.post('/addmovie2/:voteId', auth.verifytoken, userModelMin.addMovie2)
userRouter.post('/votemovie1/:voteId',  userModelMin.voteMovie1)
userRouter.post('/votemovie2/:voteId', userModelMin.voteMovie2)
userRouter.get('/getresult/:movieId', userModelMin.getResult)
// userRouter.post('/login', userModelMin.login)

export default userRouter